export default {
  props: {
    time: {
      type: [Boolean, Number],
      required: true
    },
    progress: {
      type: Boolean,
      required: true
    }
  },
  computed: {
    getTransition() {
      return this.progress ? `transition: width ${this.time}ms linear;width:0;` : ``;
    }
  }
};
