  methods: {
    setData(data) {
      this.data = data;
    },
    close() {
      this.$callClient("C:DiningModal:Close");
    },
    onkeyup(e) {
      if (e.keyCode === 27 && !window.isModalShow) {
        this.close();
      }
    }
  },
  mounted() {
    this.$onClient("C:DiningModal:SetData", this.setData);
    this.$callClient("C:DiningModal:Ready");
    window.addEventListener("keyup", this.onkeyup);
  },
  beforeUnmount() {
    this.$offClient("C:DiningModal:SetData", this.setData);
    window.removeEventListener("keyup", this.onkeyup);
  }
};
