import BuyChips from "./Components/BuyChips.vue";
import SellChips from "./Components/SellChips.vue";
import LeftInfo from "./Components/LeftInfo.vue";
import RightInfo from "./Components/RightInfo.vue";
import Help from "./Components/Help.vue";
import Navigation from "./Components/Navigation.vue";
export default {
  data: function () {
    return {
      appActive: false,
      navigation: [{
        route: "buy",
        img: "buy",
        content: "Купить фишки"
      }, {
        route: "sell",
        img: "sell",
        content: "Продать фишки"
      }],
      route: "buy",
      help: [{
        keys: ["Esc"],
        title: "Закрыть окно"
      }],
      info: {
        balance: 123123,
        chips: 1231231,
        buyPrice: 100,
        sellPrice: 90
      }
    };
  },
  components: {
    RightInfo,
    LeftInfo,
    Help,
    Navigation,
    BuyChips,
    SellChips
  },
  computed: {
    isBuy() {
      return this.route === "buy";
    }
  },
  methods: {
    setRoute(route) {
      this.route = route;
    },
    onKeyUp(e) {
      if (e.keyCode === 27) this.closeApp();
    },
    closeApp() {
      this.appActive = false;
    },
    destroyBrowser() {
      this.$callClient("Chips:DestroyBrowser");
    },
    openChips(options) {
      this.info = options;
      if (!this.appActive) this.appActive = true;
    },
    buyChips(count) {
      this.$callClient("Chips:BuyChips", count);
    },
    sellChips(count) {
      this.$callClient("Chips:SellChips", count);
    }
  },
  mounted() {
    window.addEventListener("keyup", this.onKeyUp);
    this.$onClient("C:Chips:SetData", this.openChips);
    this.$callClient("W:Chips:Ready");
  },
  beforeUnmount() {
    window.removeEventListener("keyup", this.onKeyUp);
    this.$offClient("C:Chips:SetData", this.openChips);
  }
};
