import "core-js/modules/es.array.push.js";
export default {
  data() {
    return {
      newNickName: "",
      isInputOnFocus: false,
      isValid: null,
      isLoading: false,
      alerts: [],
      isActive: false
    };
  },
  methods: {
    onSubmit() {
      if (this.isValid) {
        this.$callClient("ChangeName.Submit", this.newNickName);
      } else {
        this.showAlert({
          status: false,
          msg: "Вы ввели никнейм нарушающий \nкритерии/правила сервера"
        });
      }
    },
    showAlert(req) {
      this.alerts.push(req);
      setTimeout(() => {
        this.alerts.shift();
      }, 3000);
    },
    checkCapital(str) {
      return str[0] === str[0].toUpperCase() && str.slice(1) === str.slice(1).toLowerCase();
    }
  },
  watch: {
    newNickName(value) {
      const regExp = /^[a-zA-Z_]*$/;
      this.isLoading = true;
      setTimeout(() => {
        this.isLoading = false;
        const splitName = value.split("_");
        this.isValid = splitName.length !== 2 ? false : splitName.every(name => regExp.test(name) && name.length > 1 && this.checkCapital(name));
      }, 400);
    }
  },
  mounted() {
    this.isActive = true;
    this.$onClient("Admin.ChangeName.Notify", this.showAlert);
  }
};
