import MPTip from "../../../MP-Tip/MP-Tip.vue";
import MPPrice from "../../../MP-Price/MP-Price.vue";
import { checkSection, getitemCardType, parseDate } from "@/Application/Pages/MarketPlace/Assets/functions";
export default {
  props: {
    itemCard: {
      type: Object,
      required: true
    }
  },
  components: {
    MPTip,
    MPPrice
  },
  computed: {
    getItemMainInfo() {
      const info = [];
      if (this.itemCard?.createdTime) {
        info.push({
          title: "Дата оголошення:",
          value: parseDate(this.itemCard.createdTime, "detailed")
        });
      }
      if (this.itemCard?.objectType && !checkSection(this.$route, "items")) {
        info.push({
          title: "Тип майна:",
          value: this.$t(getitemCardType(this.itemCard.objectType, true))
        });
      }
      if (this.itemCard?.category) {
        info.push({
          title: "Категорія:",
          value: this.itemCard.category
        });
      }
      if (this.itemCard.data?.street) {
        info.push({
          title: "Адреса:",
          value: this.itemCard.data.street
        });
      }
      if (this.itemCard.data?.number) {
        info.push({
          title: "Держ. номер:",
          value: this.itemCard.data.number
        });
      }
      if (this.itemCard.data?.carRoom) {
        info.push({
          title: "Автосалон:",
          value: this.itemCard.data.carRoom
        });
      }
      return info;
    },
    getItemTips() {
      const tips = [];
      if (this.itemCard.data?.roommates) {
        tips.push({
          value: `${this.itemCard.data.roommates}`,
          img: "competitors",
          title: "Співмешканці",
          color: "default"
        });
      }
      if (this.itemCard.data?.garageSlot) {
        tips.push({
          value: `${this.itemCard.data.garageSlot}`,
          img: "garage",
          title: "Місткість гаража",
          color: "default"
        });
      }
      if (this.itemCard.data?.fuelType) {
        tips.push({
          value: this.itemCard.data.fuelType,
          img: "fuel",
          title: "Тип палива",
          color: "blue"
        });
      }
      if (this.itemCard.data?.weight) {
        tips.push({
          value: this.itemCard.data.weight,
          img: "payload",
          title: "Вантажопід'йомність",
          color: "default"
        });
      }
      if (this.itemCard.data?.slots) {
        tips.push({
          value: this.itemCard.data.slots,
          img: "slots",
          title: "Слоти",
          color: "default"
        });
      }
      if (this.itemCard.data?.fuel) {
        tips.push({
          value: this.itemCard.data.fuel,
          img: "tankCapacity",
          title: "Обь'єм бензобака",
          color: "default"
        });
      }
      return tips;
    },
    isShowStatePrice() {
      return this.itemCard.data?.govPrice && checkSection(this.$route, "createListing");
    }
  }
};
