import { createEventEmitter } from "../Objects/EventEmitter.js";
const MPEnvironment = {
  events: {
    ...createEventEmitter(false),
    add(eventName, callback) {
      this._use(true, eventName, callback);
    },
    remove(eventName, callback) {
      this._use(false, eventName, callback);
    },
    call(eventName, ...args) {
      this._call(eventName, ...args);
    }
  },
  trigger(eventName, ...args) {
    this.events._call(eventName, ...args);
  },
  invoke(eventName, ...args) {
    console.log(`invoke: "${eventName}"`, ...args);
  }
