import { formatNumber, parseDate } from "@/Application/Pages/MarketPlace/Assets/functions";
export default {
  components: {},
  props: {
    betData: {
      type: Object,
      required: true
    },
    isMyBet: {
      type: Boolean,
      required: false,
      default: false
    }
  },
  data() {
    return {
      bet: 33
    };
  },
  methods: {
    formatPrice(num) {
      return formatNumber(num, " ");
    },
    getCreatedDate(date) {
      return parseDate(date, "detailed");
    }
  },
  computed: {
    getActionTitle() {
      if (this.isMyBet) {
        return `${this.$t("Ви підвищили ставку на")} $${this.formatPrice(this.betData.raiseCount)}`;
      } else if (this.betData?.raiseCount) {
        return `${this.$t("Підвищив ставку на")} $${this.formatPrice(this.betData.raiseCount)}`;
      } else {
        return "";
      }
    }
  }
};
