import "./Parking.scss";
import Modal from "./Components/Modal.vue";
import QuitModal from "./Components/QuitModal.vue";
export default {
  components: {
    Modal,
    QuitModal
  },
  props: {
    type: {
      type: String,
      required: true
    }
  },
  data() {
    return {
      appActive: false,
      parkingData: {}
    };
  },
  computed: {
    currentComponent() {
      return this.type === "buy" || this.type === "add" ? Modal : QuitModal;
    }
  },
  methods: {
    onKeyUp(e) {
      if (e.keyCode === 27 && !window.isModalShow) this.appActive = false;
    },
    closeApp() {
      // this.$router.push({ name: "HUD" });
      this.$callClient("Parking.Close");
    }
  },
  mounted() {
    this.$RPC.call("Parking.GetParams").then(([parkingData]) => {
      this.appActive = true;
      this.parkingData = parkingData;
      window.addEventListener("keyup", this.onKeyUp);
    });
  },
  beforeUnmount() {
    window.removeEventListener("keyup", this.onKeyUp);
  }
};
