import MPInput from "../../MP-Input/MP-Input.vue";
import MPActionBtn from "../../MP-ActionBtn/MP-ActionBtn.vue";
import { formatNumber } from "../../../Assets/functions";
import InfoBlock from "./InfoBlock.vue";
export default {
  props: {
    modalData: {
      type: Object,
      required: true
    }
  },
  data() {
    return {
      payMethods: [{
        title: "Готівкою",
        value: "cash"
      }, {
        title: "Банківською картою",
        value: "card"
      }],
      hoursQuantityInput: 0
    };
  },
  methods: {
    setHoursQuantity(value) {
      this.hoursQuantityInput = value;
    },
    buy(method) {
      if (method && this.hoursQuantityInput) {
        this.$callClient("C:MarketPlace:Listing:Rent", this.getSelectedItem.id, method, this.hoursQuantityInput);
      }
    }
  },
  computed: {
    getSelectedItem() {
      return this.$store.getters["marketPlace/getSelectedItem"];
    },
    getModalData() {
      const rentTypes = {
        days: "Подобовий",
        hours: "Погодинний"
      };
      return [[{
        title: "Назва товару",
        value: this.modalData.name
      }], [{
        title: "Тип оголошення",
        value: `Оренда транспорту`
      }], [{
        title: "Тип оренди",
        value: rentTypes[this.modalData.data.rentType] || ""
      }]];
    },
    getRentType() {
      const rentTypes = {
        days: "днів",
        hours: "годин"
      };
      return rentTypes[this.modalData.data.rentType] || "";
    },
    getRentPrice() {
      return formatNumber(this.hoursQuantityInput * this.modalData.price, " ") || 0;
    },
    getMaxRentValue() {
      const maxValues = {
        days: 7,
        hours: 24
      };
      return maxValues[this.modalData.data.rentType];
    }
  },
  components: {
    MPActionBtn,
    MPInput,
    InfoBlock
  }
};
