import "core-js/modules/es.array.push.js";
import Hints from "../Components/Hints.vue";
import Goverment from "../Components/Certificate/Goverment.vue";
import Police from "../Components/Certificate/Police.vue";
import FBI from "../Components/Certificate/FBI.vue";
import Army from "../Components/Certificate/Army.vue";
import EMS from "../Components/Certificate/EMS.vue";
import Weazel from "../Components/Certificate/Weazel.vue";
import Passport from "../Components/Passport.vue";
const Components = {
  goverment: Goverment,
  police: Police,
  fbi: FBI,
  army: Army,
  ems: EMS,
  weazel: Weazel,
  passport: Passport
};
export default {
  components: {
    Hints,
    ...Object.values(Components)
  },
  data() {
    return {
      hints: [{
        keys: ["Esc"],
        title: "Закрыть"
      }],
      data: null
    };
  },
  computed: {
    getComponent() {
      return this.data ? Components[this.$route.params.type] || null : null;
    }
  },
  methods: {
    onKeyUp(e) {
      if (e.keyCode === 27 && !window.isModalShow) this.$router.push("/");
    },
    setData(payload) {
      const nameObject = (payload.name || "").split("_").reduce((acc, cur, idx) => {
        acc[idx ? "cSurname" : "cName"] = cur;
        return acc;
      }, {});
      nameObject.cFullname = `${nameObject.cName} ${nameObject.cSurname}`;
      this.data = Object.assign({}, payload, nameObject);
    }
  },
  created() {
    window.addEventListener("keyup", this.onKeyUp);
    this.$onClient("W:DocumentsCertificate:SetData", this.setData);
    this.$callClient("C:DocumentsCertificate:Ready");
  },
  beforeUnmount() {
    window.removeEventListener("keyup", this.onKeyUp);
    this.$offClient("W:DocumentsCertificate:SetData", this.setData);
    this.$callClient("C:DocumentsCertificate:Destroyed");
  }
};
