import ActionMixin from "../Components/Action/ActionMixin.js";
export default {
  mixins: [ActionMixin],
  data() {
    return {
      account: ""
    };
  },
  methods: {
    onAccountInput(e) {
      this.onInput(e);
      if (e.target.value.length > 6) e.target.value = e.target.value.substring(0, e.target.value.length - 1);
      this.account = e.target.value;
    },
    onAction() {
      this.isButtonBlocked = true;
      setTimeout(() => {
        this.isButtonBlocked = false;
      }, 1000);
      if (this.amount && this.account) {
        this.$callClient("C:ATM:Transfer", this.account, this.amount);
        this.account = "";
        this.amount = 0;
      } else {
        this.$callLocal("W:ATM:Announcement", false);
      }
    }
  },
  created() {
    this.$callClient("C:ATM:ConversionReady");
  },
  beforeUnmount() {
    this.$callClient("C:ATM:ConversionDestroyed");
  }
};
