export default {
  data() {
    return {};
  },
  props: {
    colorHsv: {
      type: Object
    }
  },
  methods: {
    onMousedown(e) {
      this.movePicker(e);
      window.addEventListener("mousemove", this.movePicker);
      window.addEventListener("mouseup", () => {
        window.removeEventListener("mousemove", this.movePicker);
      });
    },
    movePicker(e) {
      const rect = this.$refs.colorPickerField.getBoundingClientRect();
      const clientY = e.touches ? e.touches[0].clientY : e.clientY;
      const y = clientY - rect.top;
      if (y < 0 || y > rect.height) return;
      const h = y / rect.height;
      this.$emit("change", h);
    }
  }
};
