import StatisticCarItem from "./StatisticCarItem.vue";
export default {
  data() {
    return {
      scaleIconProcent: 1
    };
  },
  props: {
    carsList: Array,
    setIsShowCar: Function
  },
  components: {
    StatisticCarItem
  },
  methods: {
    setScaleIconProcent() {
      const fontSize = getComputedStyle(document.documentElement).fontSize;
      const size = fontSize.slice(0, -2);
      this.scaleIconProcent = +size / 10;
    },
    handleScroll(event) {
      const container = event.currentTarget;
      if (event.deltaY > 0) {
        container.scrollLeft += 50;
      } else {
        container.scrollLeft -= 50;
      }
    }
  },
  mounted() {
    window.addEventListener("resize", this.setScaleIconProcent);
    this.setScaleIconProcent();
  }
};
