    isEmptyPrize() {
      return Object.values(this.prize)?.length;
    }
  },
  methods: {
    showInfoModal() {
      this.timer = setTimeout(() => this.isShowInfoModal = true, 500);
      if (this.prize.ObjectType === "AnimationItemPrize") {
        this.$refs.anim.play();
      }
    },
    hideInfoModal() {
      clearTimeout(this.timer);
      if (this.prize.ObjectType === "AnimationItemPrize") {
        this.$refs.anim.pause();
      }
      this.isShowInfoModal = false;
    },
    GetImagePath(item, isModal) {
      if (item.image.includes("cdn/")) return item.image;
      if (item.ObjectType == "AnimationItemPrize") {
        return `animations/nexus_animations_2_chicken_wing.webm`;
      }
      if (item.ObjectType == "ClothesItemPrize") {
        let index = -1;
        switch (item.ItemType) {
          case "Hat":
            index = -12;
            break;
          case "Top":
            index = -11;
            break;
          case "Leg":
            index = -4;
            break;
          case "Feet":
            index = -6;
            break;
          case "Accessories":
            index = -14;
            break;
          case "Mask":
            index = -1;
            break;
          case "Jewelry":
            index = -7;
            break;
          case "Glasses":
            index = -13;
            break;
          case "Backs":
            index = -20;
            break;
          case "BodyArmor":
            index = -9;
            break;
        }
        console.log(`cdn/inventory/clothes/${index}/${item.image}${item.variation}_${item.colors[isModal ? this.colorIndex : 0]}.png`);
        return `cdn/inventory/clothes/${index}/${item.image}${item.variation}_${item.colors[isModal ? this.colorIndex : 0]}.png`;
      }
      return `cdn/inventory/other/${item.image}.png`;
    }
  }
};
