import Code from "../../Assets/Icons/Code.vue";
import Star from "../../Assets/Icons/Star.vue";
export default {
  components: {
    Code,
    Star
  },
  props: {
    wanted: {
      type: Object,
      required: true
    }
  },
  computed: {
    getClass() {
      return this.wanted.stars === 5 ? "global" : this.wanted.stars === 4 ? "local" : "";
    },
    getName() {
      return this.wanted.name.split("_").join(" ");
    },
    getStatus() {
      return {
        title: this.wanted.watcher ? this.$t(`Отслеживает`) + ` <br> ${this.wanted.watcher}` : this.wanted.inZone ? this.$t("В зоне радара") : this.$t("Вне зоны радара"),
        button: this.wanted.watcher || !this.wanted.inZone ? true : false
      };
    }
  },
  methods: {
    acceptWanted() {
      this.$emit("acceptWanted", this.wanted.name);
    }
  }
};
