export default {
  name: "PhotoViewer",
  data() {
    return {
      scale: 1
    };
  },
  methods: {
    onScroll(e) {
      if (e.deltaY > 0) {
        if (this.scale <= 0.2) return;
        this.scale -= 0.1;
      } else {
        if (this.scale >= 2) return;
        this.scale += 0.1;
      }
    },
    hideImg() {
      this.$store.dispatch("phone/setShownPhoto", null);
    }
  },
  computed: {
    photoViewer() {
      return this.$store.getters[`phone/getIsShowPhoto`];
    },
    photoViewerSrc() {
      return typeof this.photoViewer == "object" ? this.photoViewer?.src : this.photoViewer;
    },
    photoViewerTitle() {
      return this.photoViewer?.title;
    }
  }
};
