import "core-js/modules/es.array.push.js";
import HouseIcon from "../../../../../Components/Icons/Bank/HouseIcon.vue";
import SimIcon from "../../../../../Components/Icons/Bank/SIMIcon.vue";
import BusinesIcon from "../../../../../Components/Icons/Bank/BusinesIcon.vue";
import OtherIcon from "../../../../../Components/Icons/Bank/OtherIcon.vue";
export default {
  data() {
    return {
      inputVal: "",
      actionsList: [{
        icon: SimIcon,
        catigory: "Баланс",
        name: "Сим-карты",
        route: "sim"
      }, {
        icon: HouseIcon,
        catigory: "Недвижимость",
        name: "Дом",
        route: "house"
      }, {
        icon: BusinesIcon,
        catigory: "Недвижимость",
        name: "Бизнес",
        route: "busines"
      }
      // {
      //   icon: OtherIcon,
      //   catigory: "Другие",
      //   name: "Услуги",
      //   route: "other",
      // },
      ]
    };
  },
  methods: {
    openPage(route) {
      this.$router.push(route);
    },
    showPayMenu() {
      if (this.inputVal.length >= 4) {
        this.$router.push(`/Phone/BankApp/Main/Pay/${this.onlyNumber(this.inputVal)}/false`);
      }
    },
    onlyNumber(str) {
      return str.replace(/\D/g, "");
    },
    onInput(e) {
      const value = e.target.value;
      const onlyNumber = this.onlyNumber(value);
      const formatedNumber = onlyNumber > 6 ? onlyNumber.slice(0, 6) : onlyNumber;
      this.inputVal = formatedNumber.replace(/(\d{3})(\d{3})/, "$1-$2");
    }
  }
};
