export default {
  data() {
    return {
      scaleIconProcent: null,
      playerData: {},
      requirementsData: {},
      isInputOnFocus: false,
      isValid: null,
      isLoading: false,
      newFamilyName: "",
      isActive: false
    };
  },
  methods: {
    setScaleIconProcent() {
      const fontSize = getComputedStyle(document.documentElement).fontSize;
      const size = fontSize.slice(0, -2);
      this.scaleIconProcent = +size / 10;
    },
    getStrokeDashoffset(value, requirement) {
      const procent = value >= requirement ? 100 : value / requirement * 100;
      return 100 - procent;
    },
    onSubmit() {
      if (this.isValid && this.newFamilyName) {
        this.$callClient("C:CreationFamily:create", this.newFamilyName);
      } else {
        this.$Notification.create({
          type: "error",
          layout: "compact-bottom__right",
          theme: "compact",
          time: 5000,
          isFromServer: true,
          text: this.$t("НЕКОРЕКТНОЕ НАЗВАНИЕ СЕМЬИ"),
          progressBar: false
        });
      }
    },
    formatNumber(num) {
      return num.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ".") + "$";
    },
    close() {
      this.$callClient("C:CreationFamily:close");
    },
    onKeyUp(e) {
      if (e.key === "Escape") {
        this.close();
      }
    },
    setData(data) {
      this.playerData = data.playerData;
      this.requirementsData = data.requirementsData;
      this.drawBorder();
    },
    drawBorder() {
      this.$refs.ExpPath.style.strokeDashoffset = this.getStrokeDashoffset(this.playerData.lvl, this.requirementsData.lvl);
      this.$refs.DollarPath.style.strokeDashoffset = this.getStrokeDashoffset(this.playerData.money, this.requirementsData.money);
    }
  },
  watch: {
    newFamilyName(val) {
      this.isValid = val.length > 4 && val.length < 15;
      this.isLoading = true;
      setTimeout(() => this.isLoading = false, 100);
    },
    requirementsData() {
      this.drawBorder();
    }
  },
  mounted() {
    this.setScaleIconProcent();
    this.isActive = true;
    this.$onClient("C:CreationFamily:setData", this.setData);
    this.$callClient("C:CreationFamily:Ready");
    window.addEventListener("keyup", this.onKeyUp);
  },
  beforeUnmount() {
    this.$offClient("C:CreationFamily:setData", this.setData);
    window.removeEventListener("keyup", this.onKeyUp);
  }
};
