import NavigationMixin from "../../mixins/controls/organization/Navigation.js";
import MembersMixin from "../../mixins/controls/organization/Members.js";
import LogsMixin from "../../mixins/controls/organization/Logs.js";
import RanksMixin from "../../mixins/controls/organization/Ranks.js";
import VehiclesMixin from "../../mixins/controls/organization/Vehicles.js";
import ActionsMixin from "../../mixins/controls/organization/Actions.js";
import Decoration from "../../components/controls/global/Decoration.js";
import Modal from "../../components/controls/organization/Modal.js";
import Lock from "../../components/controls/global/Lock.js";
import Header from "../../components/controls/global/Header.js";
import Help from "../../components/global/Help.js";
export default {
  components: {
    Header,
    Help,
    Decoration,
    Lock,
    Modal
  },
  mixins: [NavigationMixin, MembersMixin, LogsMixin, RanksMixin, VehiclesMixin, ActionsMixin],
  data: function () {
    return {
      appActive: !false,
      hasAccess: true,
      help: [{
        keys: ["Esc"],
        title: "Закрыть окно"
      }],
      shortRanks: [],
      organizationName: "",
      organizationType: ""
    };
  },
  computed: {
    getTitle() {
      return this.organizationType === "fraction" ? "Управление фракцией" : "Управление семьей";
    }
  },
  methods: {
    onKeyUp(e) {
      if (e.key === "Escape") this.closeApp();
    },
    closeApp() {
      this.appActive = false;
    },
    destroyBrowser() {
      this.$callClient("Controls:DestroyBrowser");
    },
    setData(organizationType, organizationName) {
      this.organizationType = organizationType;
      this.organizationName = organizationName;
      window.organizationType = organizationType;
      window.organizationName = organizationName;
    },
    clearData() {}
  },
  mounted() {
    window.addEventListener("keyup", this.onKeyUp);
  }
};
