import MPPrice from "../../../MP-Price/MP-Price.vue";
import ActionBtn from "../../../MP-ActionBtn/MP-ActionBtn.vue";
import BetItem from "./Components/BetItem/BetItem.vue";
import { formatNumber } from "@/Application/Pages/MarketPlace/Assets/functions";
export default {
  components: {
    MPPrice,
    ActionBtn,
    BetItem
  },
  methods: {
    makeBet(btnIdx) {
      this.$callClient("C:MarketPlace:Auction:MakeBet", this.getSelectedItem.id, btnIdx);
    },
    formatNumber(num) {
      return formatNumber(num, ",");
    },
    scrollToBottom() {
      const list = this.$refs.scrollList;
      if (list) {
        list.scrollTo({
          top: list.scrollHeight,
          behavior: "smooth"
        });
      }
    }
  },
  watch: {
    getBiddingList: {
      handler(newVal, oldVal) {
        setTimeout(this.scrollToBottom, 100);
      },
      deep: true
    }
  },
  computed: {
    getStatic() {
      return this.$store.getters["playerMenu/getStatic"];
    },
    getBiddingList() {
      return this.getSelectedItem.data.biddingList;
    },
    getSelectedItem() {
      return this.$store.getters["marketPlace/getSelectedItem"];
    },
    getCoefficients() {
      return this.$store.getters["marketPlace/getCoefficients"];
    }
  }
};
