import IsCheckIcon from "./IsCheckIcon.vue";
import ArrowIcon from "./ArrowIcon.vue";
import PhoneMixin from "../../../../Mixins/PhoneMixin";
export default {
  props: {
    msg: {
      type: Object,
      required: true
    }
  },
  mixins: [PhoneMixin],
  computed: {
    numbers() {
      return this.$store.getters["phone/getNumbers"];
    },
    getTime() {
      const date = new Date(this.msg.messages[0]?.date);
      return date.getHours() + ":" + (date.getMinutes() > 10 ? date.getMinutes() : "0" + date.getMinutes());
    }
  },
  methods: {
    formatText(text) {
      return text.length > 50 ? text.slice(0, 50).trim() + "..." : text;
    }
  },
  components: {
    IsCheckIcon,
    ArrowIcon
  }
};
