export default {
  data() {
    return {
      socialImgPath: ""
    };
  },
  props: {
    social: {
      type: String,
      default: ""
    }
  },
  methods: {
    getSocialImgPath() {
      const imageUrl = `http://a.rsg.sc/n/${this.social}/s`;
      var img = new Image();
      img.onload = () => {
        this.socialImgPath = `http://a.rsg.sc/n/${this.social}/s`;
      };
      img.onerror = () => {
        this.socialImgPath = `https://as1.ftcdn.net/v2/jpg/03/46/83/96/1000_F_346839683_6nAPzbhpSkIpb8pmAwufkC7c5eD7wYws.jpg`;
      };
      img.src = imageUrl;
    }
  },
  mounted() {
    this.getSocialImgPath();
  }
};
