import "core-js/modules/es.array.push.js";
import MPSortSelect from "../../../../MP-SortSelect/MP-SortSelect.vue";
import MPPrice from "../../../../MP-Price/MP-Price.vue";
export default {
  components: {
    MPSortSelect,
    MPPrice
  },
  props: {
    itemCard: {
      type: Object,
      required: true
    }
  },
  data() {
    return {
      select: {
        title: "Вибір проміжку часу",
        options: ["Рік", "Місяць", "Неділя", "День"]
      }
    };
  },
  computed: {
    getBusinessData() {
      const pricesData = [];
      const infoData = [];
      if (this.itemCard?.data.bizData?.revenue) {
        pricesData.push({
          title: "Виручка",
          value: this.itemCard.data.bizData.revenue
        });
      }
      if (this.itemCard?.data.bizData?.profit) {
        pricesData.push({
          title: "Чистий прибуток",
          value: this.itemCard.data.bizData.profit
        });
      }
      if (this.itemCard?.data.bizData?.averageBill) {
        pricesData.push({
          title: "Середній чек",
          value: this.itemCard.data.bizData.averageBill
        });
      }
      if (this.itemCard.data?.bizData?.sellCount) {
        infoData.push({
          title: "Кількість продажів",
          value: this.itemCard.data.bizData.sellCount
        });
      }
      if (this.itemCard.data.bizData?.profitability) {
        infoData.push({
          title: "Рентабельність",
          value: `${this.itemCard.data.bizData.profitability}%`
        });
      }
      return {
        pricesData: pricesData,
        infoData: infoData
      };
    }
  }
};
