export default {
  template: `
    <div class="store-backpacks__info">
        <div class="store-backpacks__info-row">
            <p>Количество слотов</p>
            <div class="store-backpacks__row-block">
                <p>{{dSlots}}</p>
            </div>
        </div>
        <div class="store-backpacks__info-row">
            <p>Вес</p>
            <div class="store-backpacks__row-block">
                <p>{{dWeight}}</p>
            </div>
        </div>
    </div>
    `,
  props: {
    activeModification: {
      type: Object
    }
  },
  computed: {
    dWeight() {
      return this.activeModification && this.activeModification.weight ? this.activeModification.weight : ``;
    },
    dSlots() {
      return this.activeModification && this.activeModification.slots ? this.activeModification.slots : ``;
    }
  }
};
