import { mapGetters } from "vuex";
import Notification from "./Notification.vue";
export default {
  components: {
    Notification
  },
  props: {
    layout: {
      type: String,
      required: true
    }
  },
  computed: {
    ...mapGetters("notification", ["getLayout"]),
    layoutClass() {
      return `notification-layout__${this.layout}`;
    },
    layoutNotifications() {
      let notifications = this.getLayout(this.layout);
      return {
        notifications,
        count: notifications.length
      };
    }
  }
};
