import Tilt from "vanilla-tilt-vue";
export default {
  data() {
    return {
      isShowInfo: false
    };
  },
  methods: {
    getBusinesnfo(info) {
      return `- ${this.$t(info.name)}: ${(typeof info.value === "string" ? this.$t(info.value) : this.convertToMoneyFormat(info.value)) + " " + (info?.units ? this.$t(info?.units) : "")}`;
    },
    convertToMoneyFormat(numb) {
      return new Intl.NumberFormat("de-DE").format(numb);
    },
    getBusinesColor(ownerName, holderName, isAttacked) {
      if (isAttacked) {
        return "white";
      }
      return ownerName === holderName ? "#C9F32D" : "#E8262C";
    },
    formatTime(seconds) {
      if (seconds == 0) return `00:00:00`;
      seconds += this.passedTime;
      let hours = Math.floor(seconds / 3600);
      let minutes = Math.floor((seconds - hours * 3600) / 60);
      let remainingSeconds = seconds - hours * 3600 - minutes * 60;
      if (hours < 10) {
        hours = "0" + hours;
      }
      if (minutes < 10) {
        minutes = "0" + minutes;
      }
      if (remainingSeconds < 10) {
        remainingSeconds = "0" + remainingSeconds;
      }
      return `${hours}:${minutes}:${remainingSeconds}`;
    }
  },
  components: {
    Tilt
  },
  props: {
    businesData: Object,
    passedTime: Number,
    ownerName: String,
    showModal: Function
  }
};
