export default {
  props: {
    name: {
      type: String,
      required: true
    },
    max: {
      type: Number,
      required: true
    },
    current: {
      type: Number,
      required: true
    }
  },
  computed: {
    currentRotation() {
      return `transform: rotate(${360 / this.max * this.current}deg);`;
    }
  },
  methods: {
    onScroll(e) {
      let delta = e.deltaY > 0 ? -1 : 1;
      this.$emit("valueChanged", delta);
    }
  }
};
