import Tooltip from "../global/Tooltip.vue";
import LevelWings from "../assets/icons/level-wings__1.vue";
import IconClock1 from "../assets/icons/clock-1.vue";
import IconCash1 from "../assets/icons/cash-1.vue";
import IconBankCard1 from "../assets/icons/bank-card__1.vue";
import IconCompass1 from "../assets/icons/compass-1.vue";
import IconTrash1 from "../assets/icons/trash-1.vue";
import IconCalendar1 from "../assets/icons/calendar-1.vue";
import IconUser3 from "../assets/icons/user-3.vue";
import IconJudicialHammer1 from "../assets/icons/judicial-hammer__1.vue";
export default {
  components: {
    Tooltip,
    LevelWings,
    IconClock1,
    IconCash1,
    IconBankCard1,
    IconCompass1,
    IconTrash1,
    IconCalendar1,
    IconUser3,
    IconJudicialHammer1
  },
  props: {
    card: {
      require: true,
      type: Object
    }
  },
  data() {
    return {
      cardInformationLevelStrokeMax: 255
    };
  },
  methods: {
    getCardInformationLevelProgressMax(currentLevel) {
      return 3 + currentLevel * 3;
    },
    getCardInformationLevelBarStroke(card) {
      if (card.data.character[3] <= this.getCardInformationLevelProgressMax(card.data.character[2])) {
        return this.cardInformationLevelStrokeMax - card.data.character[3] * this.cardInformationLevelStrokeMax / this.getCardInformationLevelProgressMax(card.data.character[2]);
      }
    },
    hourFormate(hour) {
      if (hour === 0) {
        return this.$t("часов");
      } else if (hour === 1) {
        return this.$t("час");
      } else if (hour > 1 && hour < 5) {
        return this.$t("часа");
      } else if (hour > 4) {
        return this.$t("часов");
      }
    }
  }
};
