export default {
  template: `
    <div class="controls-list__line-item">
        <div class="controls-line__item-default title">
            <p>{{$GetText(title)}}</p>
        </div>
        <div class="controls-line__item-default switch">
            <label class="controls-switch__checkbox">
                <input type="checkbox" :checked="value" @change="changeValue">
                <span class="controls-switch__checkbox-appearance">
                    <svg viewBox="0 0 16 10" fill="none" xmlns="http://www.w3.org/2000/svg"><path d="M1 4.2L6.25 9L15 1" stroke-width="2" stroke-linecap="round" stroke-linejoin="round"/></svg>      
                </span>
            </label>
        </div>
    </div>
    `,
  props: {
    title: {
      type: String,
      required: true
    },
    value: {
      type: Boolean
    }
  },
  methods: {
    changeValue(e) {
      this.$emit('input', e.target.checked);
    }
  }
};
