import PhoneMixin from "../../../../../Mixins/PhoneMixin";
import HouseIcon from "../../../../../Components/Icons/Bank/HouseIcon.vue";
import SimIcon from "../../../../../Components/Icons/Bank/SIMIcon.vue";
import BusinesIcon from "../../../../../Components/Icons/Bank/BusinesIcon.vue";
import OtherIcon from "../../../../../Components/Icons/Bank/OtherIcon.vue";
import Outcome from "../../../../../Components/Icons/Bank/Outcome.vue";
import Income from "../../../../../Components/Icons/Bank/Income.vue";
export default {
  name: "History",
  mixins: [PhoneMixin],
  data() {
    return {
      bindIcons: {
        house: HouseIcon,
        sim: SimIcon,
        busines: BusinesIcon,
        other: OtherIcon,
        outcome: Outcome,
        income: Income
      },
      bindColors: {
        house: "#35A9DC",
        sim: "#FF9500",
        busines: "#5E5CE6",
        other: "#4CD964",
        outcome: "#FF2D55",
        income: "#30D158"
      }
    };
  },
  methods: {
    formatData(data) {
      const date = new Date(data);
      const year = date.getFullYear();
      const month = String(date.getMonth() + 1).padStart(2, "0"); // Месяцы начинаются с 0 в JavaScript
      const day = String(date.getDate()).padStart(2, "0");
      const hours = String(date.getHours()).padStart(2, "0");
      const minutes = String(date.getMinutes()).padStart(2, "0");
      const formattedDate = `${month}.${day}.${year} ${hours}:${minutes}`;
      return formattedDate;
    }
  },
  computed: {
    getHistory() {
      return this.$store.getters["phone/getBankData"].bankHistory;
    }
  }
};
