  methods: {
    convertDateTime(datetimeStr, type) {
      var parts = datetimeStr.split("/");
      var datePart = type === "date" ? parts[0] + "." + parts[1] + "." + parts[2] : parts[3] + ":" + parts[4];
      return datePart;
    },
    formatMoney(number) {
      return number;
    },
    setData(data) {
      this.DefendantsHistoryTable = data;
    },
    close() {
      this.$callClient("C:DefendantsHistory:Leave");
    },
    onKeyUp(e) {
      if (e.key === "Escape") {
        this.close();
      }
    }
  },
  created() {
    this.$onClient("W:DefendantsHistory:setData", this.setData);
    this.$callClient("C:DefendantsHistory:Ready");
    window.addEventListener("keyup", this.onKeyUp);
  },
  beforeUnmount() {
    this.$offClient("W:DefendantsHistory:setData", this.setData);
    this.$callClient("C:DefendantsHistory:closePage");
    window.removeEventListener("keyup", this.onKeyUp);
  }
};
