import Modal from "../../../../Components/UniversalModal/UniversalModal.vue";
import { MapNames, GameModes } from "../Components/ArenaEnums.js";
export default {
  components: {
    Modal
  },
  inject: ["getRooms"],
  data() {
    return {
      activeRoom: null,
      password: ""
    };
  },
  computed: {
    rooms() {
      return this.getRooms();
    }
  },
  methods: {
    joinRoom(room) {
      if (room.password) {
        this.activeRoom = room;
      } else {
        this.$callClient("Arena:Client:JoinRoom", room.id);
      }
    },
    onKeyUp(e) {
      if (e.keyCode === 27 && !window.isModalShow && !this.activeRoom) {
        this.closeMenu();
      }
    },
    closeMenu() {
      this.$callClient("Arena:Client:CloseMenu");
    },
    onModalClose() {
      this.activeRoom = null;
      this.password = "";
    },
    onPasswordInput() {
      if (this.password) this.$callClient("Arena:Client:JoinRoom", this.activeRoom.id, this.password);
      this.onModalClose();
    },
    getGameName(game) {
      return GameModes[game] || "";
    },
    getMapName(map) {
      return MapNames[map] || "";
    }
  },
  created() {
    window.addEventListener("keyup", this.onKeyUp);
  },
  beforeUnmount() {
    window.removeEventListener("keyup", this.onKeyUp);
  }
};
