import MPInput from "../../MP-Input/MP-Input.vue";
import MPActionBtn from "../../MP-ActionBtn/MP-ActionBtn.vue";
import InfoBlock from "./InfoBlock.vue";
import { formatNumber } from "../../../Assets/functions";
export default {
  props: {
    modalData: {
      type: Object,
      required: true
    }
  },
  data() {
    return {
      payMethods: [{
        title: "Готівкою",
        value: "cash"
      }, {
        title: "Банківською картою",
        value: "card"
      }],
      hoursQuantityInput: null,
      differenceHours: 0
    };
  },
  methods: {
    continueListing(method) {
      if (this.hoursQuantityInput) {
        this.$callClient("C:MarketPLace:Listing:Continue", this.modalData.id, this.hoursQuantityInput, method);
      }
    },
    setHoursQuantity(value) {
      this.hoursQuantityInput = value;
    },
    getHoursDifference() {
      const currentTime = this.modalData?.continueTime ? new Date(this.modalData.continueTime) : new Date();
      const endTime = new Date(this.modalData.endTime);
      const timeDifference = Math.abs(endTime - currentTime);
      const differenceHours = Math.floor(timeDifference / (1000 * 60 * 60) + 1);
      const maxHours = this.getListingMaxPostingHours;
      const remainingTime = (maxHours - differenceHours).toFixed(0);
      if (remainingTime > 0) {
        this.differenceHours = +remainingTime;
      } else {
        this.differenceHours = 0;
      }
    }
  },
  components: {
    MPActionBtn,
    MPInput,
    InfoBlock
  },
  mounted() {
    this.getHoursDifference();
  },
  computed: {
    getModalInfo() {
      return [[{
        title: "Назва оголошення",
        value: this.modalData?.name || ""
      }], [{
        title: "Підсумкова вартість",
        value: `${formatNumber(this.getListingUploadPrice, " ") || 0} $`
      }]];
    },
    getListingMinPostingHours() {
      return this.$store.getters["marketPlace/getListingMinPostingHours"];
    },
    getListingMaxPostingHours() {
      return this.$store.getters["marketPlace/getListingMaxPostingHours"];
    },
    getListingPricePerHour() {
      return this.$store.getters["marketPlace/getListingPricePerHour"];
    },
    getListingUploadPrice() {
      return this.hoursQuantityInput * this.getListingPricePerHour;
    }
  }
};
