import Battery from "../Components/Icons/Header/BatteryIcon.vue";
import Signal from "../Components/Icons/Header/SignalIcon.vue";
import FlyMode from "../Components/Icons/Header/FlyModeIcon.vue";
export default {
  components: {
    Battery,
    Signal,
    FlyMode
  },
  data() {
    return {
      currentTime: "",
      interval: null
    };
  },
  computed: {
    getDate() {
      const date = new Date();
      const hours = date.getHours();
      const minutes = date.getMinutes();
      return `${hours}:${minutes}`;
    },
    getSettingsData() {
      return this.$store.getters["phone/getSettingsData"];
    }
  },
  created() {
    this.updateTime();
    this.interval = setInterval(this.updateTime, 1000);
  },
  beforeUnmount() {
    clearInterval(this.interval);
  },
  methods: {
    updateTime() {
      const date = new Date();
      const hours = String(date.getHours()).padStart(2, "0");
      const minutes = String(date.getMinutes()).padStart(2, "0");
      this.currentTime = `${hours}:${minutes}`;
    }
  }
};
