import HouseIcon from "@/Application/Pages/Phone/Components/Icons/Bank/HouseIcon.vue";
import PhoneMixin from "@/Application/Pages/Phone/Mixins/PhoneMixin.vue";
export default {
  name: "House",
  data() {
    return {
      inputVal: ""
    };
  },
  mixins: [PhoneMixin],
  components: {
    HouseIcon
  },
  computed: {
    getBusines() {
      return this.$store.getters["phone/getBankData"].houseData;
    },
    bankData() {
      return this.$store.getters["playerMenu/getBankData"].PlayerStats.PlayerData.Bank;
    }
  },
  methods: {
    onPay(i) {
      this.$callClient("W:Phone:PayHouse", this.onlyNumber(this.inputVal));
      this.inputVal = "";
    },
    onlyNumber(str) {
      return str.replace(/\D/g, "");
    },
    onInput(e) {
      const value = e.target.value;
      const onlyNumber = this.onlyNumber(value);
      if (!onlyNumber) {
        this.inputVal = onlyNumber;
        return;
      }
      if (onlyNumber[0] == 0) {
        this.inputVal = this.formatMoney(onlyNumber.slice(1));
        return;
      }
      if (this.bankData < onlyNumber) {
        this.inputVal = this.formatMoney(this.bankData) + "$";
        return;
      }
      this.inputVal = this.formatMoney(onlyNumber) + "$";
    },
    onKeydown(e) {
      if (e.keyCode === 8) {
        this.inputVal = this.formatMoney(this.onlyNumber(this.inputVal).slice(0, -1)) + "$";
      }
    }
  }
};
