    if (!mp._instance) {
      this.events = {
        list: new Map(),
        add(event, callback) {
          this.list.set(event, callback);
        }
      };
      mp._instance = this;
    }
    return mp._instance;
  }
  trigger(event, ...args) {
    console.log(event, args);
