import store from "../Store/Store.js";
class xNotification {
  constructor() {
    if (!xNotification._instance) {
      xNotification._instance = this;
    }
    return xNotification._instance;
  }
  create(notification) {
    let defaultSettings = store.state.notification.defaultSettings;
    for (let property in defaultSettings) {
      if (notification[property] === undefined) notification[property] = defaultSettings[property];
    }
    store.commit("notification/addNotification", notification);
  }
}
export default {
  install: app => {
    app.config.globalProperties.$Notification = new xNotification();
  }
};
