import ActionMixin from "../Components/Action/ActionMixin.js";
export default {
  mixins: [ActionMixin],
  data() {
    return {
      numbers: [100, 500, 1000, 10000]
    };
  },
  computed: {
    getType() {
      return this.$route.params.type;
    },
    getTitle() {
      return this.getType == "put" ? "Пополнение счета" : "Снятие наличных";
    },
    getButtonText() {
      return `${this.getType == "put" ? "Пополнить" : "Снять"} {0}$`;
    }
  },
  methods: {
    isNumberActive(number) {
      return this.amount == number;
    },
    setActiveNumber(number) {
      if (!this.isNumberActive(number)) this.amount = number;
    },
    onAction() {
      this.isButtonBlocked = true;
      setTimeout(() => {
        this.isButtonBlocked = false;
      }, 1000);
      if (this.amount) {
        this.$callClient("C:ATM:Manage", this.getType, this.amount);
        this.amount = 0;
      }
    }
  },
  created() {
    this.$callClient("C:ATM:ManagementReady");
  },
  beforeUnmount() {
    this.$callClient("C:ATM:ManagementDestroyed");
  }
};
