import MPTimer from "../../../MP-Timer/MP-Timer.vue";
import MPSpecialBtn from "../../../MP-SpecialBtn/MP-SpecialBtn.vue";
import ActionBtn from "../../../MP-ActionBtn/MP-ActionBtn.vue";
import MPTip from "../../../MP-Tip/MP-Tip.vue";
import MPGender from "../../../MP-Gender/MP-Gender.vue";
import ItemShortInfo from "../ItemShortInfo/ItemShortInfo.vue";
import ItemSpecialInfo from "../ItemSpecialInfo/ItemSpecialInfo.vue";
import ItemMainInfo from "../ItemMainInfo/ItemMainInfo.vue";
import { checkSection, getItemCardImgPath, getIsImgFull } from "@/Application/Pages/MarketPlace/Assets/functions";
import CdnResolverImage from "@/Components/CdnResolverImage.vue";
import MPImgPicker from "../../../MP-ImgPicker/MP-ImgPicker.vue";
export default {
  props: {},
  data() {
    return {
      playerImg: null,
      currentImgPath: null
    };
  },
  components: {
    MPTimer,
    MPSpecialBtn,
    ActionBtn,
    MPTip,
    ItemShortInfo,
    ItemSpecialInfo,
    ItemMainInfo,
    MPGender,
    CdnResolverImage,
    MPImgPicker
  },
  methods: {
    setImg(path) {
      this.currentImgPath = path;
    },
    getItemCardImgPath() {
      return getItemCardImgPath(this.getSelectedItem);
    },
    getIsImgFull() {
      return getIsImgFull(this.getSelectedItem.objectType);
    },
    onGPS() {
      this.$callClient("W:Phone:GoToCoords", this.getSelectedItem.data?.coords);
    }
  },
  computed: {
    getItemImgTips() {
      if (this.getSelectedItem?.bidders && this.getSelectedItem?.count && checkSection(this.$route, "auction")) {
        return [{
          value: `${this.getSelectedItem.bidders} ${this.$t("чол.")}`,
          img: "competitors"
        }, {
          value: `${this.getSelectedItem.count} ${this.$t("шт.")}`,
          img: "quantity"
        }];
      } else {
        return "";
      }
    },
    isShowTimer() {
      return this.$route.params.section === "auction" && this.getSelectedItem?.endTime;
    },
    getSelectedItem() {
      return this.$store.getters["marketPlace/getSelectedItem"];
    },
    isShowGPS() {
      return checkSection(this.$route, "auction") && this.getSelectedItem.data?.street;
    },
    getIsShowFavorite() {
      return this.$route.params?.section !== "moderation";
    }
  }
};
