import { mapGetters } from "vuex";
import api from "../mixins/api.js";
import requestGetBuilder from "../functions/requestGetBuilder.js";
import IconArrow2 from "../assets/icons/arrow-2.vue";
export default {
  mixins: [api],
  components: {
    IconArrow2
  },
  data() {
    return {
      news: {}
    };
  },
  computed: {
    ...mapGetters("global", ["getCdnResolver"]),
    ...mapGetters("authentication", ["getNews"])
  },
  methods: {
    returnBack(route) {
      this.getNews.selected = null;
      this.$router.push(route);
    }
  },
  mounted() {
    if (!this.getNews.selected) {
      return this.$router.push("/authentication/news");
    }
    requestGetBuilder([{
      key: "id",
      value: this.getNews.selected
    }]).then(query => {
      this.APIgetNewsRequest(query).then(data => {
        if (data.status) {
          this.news = data.news[0];
        }
      }).catch(() => {
        this.$Notification.create({
          theme: "compact",
          type: "error",
          layout: "bottomRight",
          time: 5000,
          text: "Ошибка при подключение к серверу. Попробуйте позже."
        });
        setTimeout(() => {
          this.returnBack("/authentication/login");
        }, 1000);
      });
    });
  }
};
