import Item from "../../components/reality/Item.js";
import Modal from "../../components/reality/Modal.js";
import SpecialItem from "../../components/reality/SpecialItem.js";
export default {
  components: {
    Item,
    SpecialItem,
    Modal
  },
  data: function () {
    return {
      appActive: false,
      housesTypes: [],
      houses: [],
      activeHouse: null,
      infoPrice: 150,
      modalOption: {},
      modalOpen: false
    };
  },
  computed: {
    closeText() {
      return this.activeHouse ? `Вернуться` : `Закрыть`;
    }
  },
  methods: {
    onKeyUp(e) {
      if (e.key === "Escape") this.closeReality();
    },
    closeClick() {
      if (!this.activeHouse) this.closeReality();else {
        this.activeHouse = "";
        this.houses = [];
      }
    },
    closeReality() {
      this.closeModal();
      this.activeHouse = null;
      this.appActive = false;
    },
    destroyBrowser() {
      this.$callClient("Reality:DestroyBrowser");
    },
    openReality(houses) {
      this.appActive = true;
      this.housesTypes = houses;
    },
    searchReality(houseID) {
      this.$callClient("Reality:SearchReality", houseID);
    },
    searchRealityCallback(houseID, houses) {
      this.activeHouse = houseID;
      this.houses = houses;
    },
    getHouseInfo(house) {
      this.$callClient("Reality:GetHouseInfo", JSON.stringify(house));
    },
    getHouseInfoCallback(house) {
      this.modalOption = house;
      this.modalOpen = true;
    },
    closeModal() {
      this.modalOption = {};
      this.modalOpen = false;
    },
    getDirections() {
      this.$callClient("Reality:GetDirections", JSON.stringify(this.modalOption));
      this.closeReality();
    }
  },
  mounted() {
    window.addEventListener("keyup", this.onKeyUp);
  }
};
