    filteredRecipes() {
      return this.recipes.filter(recipe => recipe.ItemName.toLowerCase().includes(this.inputValue.toLowerCase()));
    }
  },
  props: {
    navList: Array,
    recipes: Array,
    selectedRecipe: Object,
    selectedNavId: Number
  }
};
