import Tilt from "vanilla-tilt-vue";
import CustomRange from "../CustomRange/CustomRange.vue";
export default {
  data() {
    return {
      formModalData: {}
    };
  },
  props: {
    UniversalModalData: Object
  },
  watch: {
    UniversalModalData(newValue) {
      newValue.content.forEach(contentItem => {
        if (contentItem?.options?.valuesName) {
          this.formModalData[contentItem.options.valuesName] = contentItem.options.defaultValue ? contentItem.options.defaultValue : "";
        }
      });
    }
  },
  methods: {
    getModalDescriptionTemaplate(contentItem) {
      return `
      <div class="UniversalModal__description">
       ${contentItem.value}
      </div>`;
    },
    setInputValueOnState(value, valuesName) {
      console.log(valuesName);
      if (valuesName.isNameSection) {
        this.formModalData[valuesName.valuesName] = value.replace(/[^a-zA-Z_]/g, "");
      } else if (valuesName.isOnlyNumber) {
        this.formModalData[valuesName.valuesName] = value.replace(/[^+\d]/g, "");
      } else {
        this.formModalData[valuesName.valuesName] = value;
      }
    },
    onkeyup(e) {
      if (e.keyCode === 27 && window.isModalShow) {
        this.onClickButton(this.UniversalModalData?.buttons?.cansel);
      }
    },
    onClickButton(buttonData) {
      if (buttonData.event) {
        this.$callClient(buttonData.event, this.formModalData);
      }
      if (this.UniversalModalData.isSetToStore) {
        const data = {
          name: this.UniversalModalData.title,
          value: this.formModalData
        };
        this.$store.dispatch("universalModal/AddFormModalData", data);
      }
      if (buttonData.handler) {
        buttonData.handler();
      }
      this.$UniversalModal.remove();
    }
  },
  mounted() {
    window.addEventListener("keyup", this.onkeyup);
    var elementsWithTypeExample = document.querySelectorAll('[data-type="img"]');

    // Заменяем каждый элемент на изображение
    elementsWithTypeExample.forEach(function (element) {
      var imgElement = document.createElement("img");
      var imagePath = element.getAttribute("data-img");
      imgElement.setAttribute("src", require(`@/Application/Pages/CarShowroom/Assets/${imagePath}`));
      element.parentNode.replaceChild(imgElement, element);
    });
  },
  beforeUnmount() {
    window.removeEventListener("keyup", this.onkeyup);
  },
  components: {
    Tilt,
    CustomRange
  }
};
