import store from "../Store/Store.js";
class UniversalModal {
  create(universalModalData) {
    store.commit("universalModal/addUniversalModal", universalModalData);
  }
  remove() {
    store.commit("universalModal/removeUniversalModal");
  }
}
export default {
  install: app => {
    app.config.globalProperties.$UniversalModal = new UniversalModal();
  }
};
