    getName() {
      const {
        numbers,
        callingData
      } = this;
      const contact = numbers[callingData.number];
      return contact ? contact.name : callingData.number;
    },
    getCallImg() {
      return this.numbers[this.callingData.number]?.img;
    }
  },
