import "jquery-ui-dist/jquery-ui.js";
import ApplicationComponent from "./Application/Application.vue";
import Directives from "./Directives/Directives.js";
import EBus from "./Plugins/EventBus.js";
import Functions from "./Plugins/Functions.js";
import Helpers from "./Plugins/Helpers.js";
import Notification from "./Plugins/pNotification.js";
import RPC from "./Plugins/RPC.js";
import Router from "./Router/Router.js";
import Store from "./Store/Store.js";
import UniversalModal from "./Plugins/universalModal.js";
import { createApp } from "vue";
import createEnviroment from "./Environment/Environment.js";
import createI18N from "./Localization/I18N.js";
import mp from "./Plugins/mp.js";
const isDebug = true;
const isEnvironmentDebug = false;
const Locale = "ua";
if (!window.mp) window.mp = mp;
createEnviroment(isDebug, isEnvironmentDebug).then(({
  Environment,
  EnvironmentPlugin
}) => {
  window.Environment = Environment;
  const Application = createApp(ApplicationComponent).use(Router).use(EnvironmentPlugin).use(createI18N(Locale)).use(Functions).use(Helpers).use(Directives).use(EBus).use(RPC).use(Store).use(Notification).use(UniversalModal).mount("#app");
  window.Application = Application;
  window.AppData = Application.$store;
  window.RPC = Application.$RPC;
  window.RPC.call("Global:GetTime").then(([time]) => {
    window.AppData.commit("global/updateTime", time);
    setInterval(() => window.AppData.commit("global/updateTime"), 1000);
  });
  window.Notification = Application.$Notification;
  Router.beforeEach((to, from, next) => {
    if (Store.getters["getIsPreventRouting"]) {
      const notification = {
        type: "warning",
        layout: "compact-bottom__right",
        theme: "compact",
        time: 5000,
        text: `
                    <div class="notification-text__item">
                        <div class="notification-text__item-title">
                            <span>${Application.$t("Перемещение по навигации запрещено")} </span>
                        </div>
                    </div>
                    <div class="notification-text__item">
                        <div class="notification-text__item-icon">
                            <span class="warning-info"></span>
                        </div>
                    </div>
                `,
        progressBar: false
      };
      Store.commit("notification/addNotification", notification);
      next(false);
    } else {
      mp.trigger("SPA1.SetCurrentRoute", to.fullPath);
      next();
    }
  });
}).catch(exception => {
  console.log(exception);
});
