import MPInput from "../../MP-Input/MP-Input.vue";
import MPActionBtn from "../../MP-ActionBtn/MP-ActionBtn.vue";
import InfoBlock from "./InfoBlock.vue";
import { formatNumber } from "../../../Assets/functions";
export default {
  props: {
    modalData: {
      type: Object,
      required: true
    }
  },
  computed: {
    getSelectedItem() {
      return this.$store.getters["marketPlace/getSelectedItem"];
    },
    getTypeName() {
      const types = {
        car: "Назва транспорту",
        house: "Назва будинку",
        apart: "Назва квартири",
        item: "Назва товару",
        business: "Назва бизнесу",
        service: "Назва оголошення"
      };
      return types[this.modalData.objectType];
    },
    getModalData() {
      return [[{
        title: this.getTypeName,
        value: this.modalData.name
      }], [{
        title: "Продавець",
        value: this.modalData.data.holder,
        static: `#${this.modalData.data.static}`
      }]];
    }
  },
  data() {
    return {
      payMethods: [{
        title: "Готівкою",
        value: "cash"
      }, {
        title: "Банківською картою",
        value: "card"
      }]
    };
  },
  methods: {
    formatNumber(num) {
      return formatNumber(num, " ");
    },
    buy(method) {
      this.$callClient("C:MarketPlace:Listing:Buy", this.getSelectedItem.id, method);
    }
  },
  components: {
    MPActionBtn,
    MPInput,
    InfoBlock
  }
};
