      let counter = Math.ceil(this.buttons.length / 7);
      for (let index = 1; index <= counter; index++) {
        computedButtons.push(this.buttons.splice(0, 7));
      }
      return computedButtons;
    }
  },
  methods: {
    setActiveButton(button) {
      this.$callClient("W:Elevator:SetActive", button.id);
    },
    setButtons(buttons) {
      this.buttons = buttons;
    },
    onKeyUp(e) {
      if (e.keyCode === 27 && !window.isModalShow) this.$callClient("C:Elevator:Destroyed");
    }
  },
  created() {
    window.addEventListener("keyup", this.onKeyUp);
    this.$onClient("W:Elevator:SetButtons", this.setButtons);
    this.$callClient("C:Elevator:Ready");
  },
  beforeUnmount() {
    window.removeEventListener("keyup", this.onKeyUp);
    this.$offClient("W:Elevator:SetButtons", this.setButtons);
  }
