import SystemAppHeader from "../../../../Components/SystemAppHeader.vue";
import PhoneAppListItem from "../Components/PhoneAppListItem.vue";
import ContactsContent from "../../../../Pages/Apps/ContactsApp/Components/ContactList.vue";
export default {
  data() {
    return {
      headerOptions: ["Все", "Пропущ."],
      selectedOption: "Все",
      isShowContactList: false
    };
  },
  components: {
    SystemAppHeader,
    PhoneAppListItem,
    ContactsContent
  },
  methods: {
    getFavoriteContactById(id) {
      return this.contacts.find(item => item.id === id);
    },
    onContactClick(item) {
      const {
        id
      } = item;
      this.addToFavorite(id);
      this.isShowContactList = false;
    },
    onAddFav() {
      this.isShowContactList = true;
    },
    onCancel() {
      this.isShowContactList = false;
    },
    addToFavorite(id) {
      this.$callClient("W:Phone:AddFavorite", id, true);
    }
  },
  computed: {
    favorites() {
      return this.$store.getters["phone/getFavorites"];
    },
    contacts() {
      return this.$store.getters["phone/getContacts"];
    }
  }
};
