import MPInput from "../../MP-Input/MP-Input.vue";
import MPActionBtn from "../../MP-ActionBtn/MP-ActionBtn.vue";
import InfoBlock from "./InfoBlock.vue";
import { formatNumber } from "../../../Assets/functions";
export default {
  props: {
    modalData: {
      type: Object,
      required: true
    }
  },
  data() {
    return {
      payMethods: [{
        title: "Готівкою",
        value: "cash"
      }, {
        title: "Банківською картою",
        value: "card"
      }],
      hoursQuantityInput: null
    };
  },
  methods: {
    upload(method) {
      if (this.hoursQuantityInput) {
        if (this.modalData.objectType !== "service") {
          if (this.$route.params?.type === "transportRent") {
            this.$callClient("C:MarketPLace:Listing:Create:Rent", this.modalData.id, this.modalData.data.comment, this.modalData.price, this.modalData.data.rentType, this.hoursQuantityInput, method, this.modalData.extraImgsId);
          } else {
            this.$callClient("C:MarketPLace:Listing:Create", this.modalData.id, this.modalData.data.comment, this.modalData.price, this.hoursQuantityInput, method, this.modalData.extraImgsId);
          }
        } else {
          this.$callClient("C:MarketPLace:Listing:Create:Service", this.modalData.name, this.modalData.data.comment, this.modalData.price, this.hoursQuantityInput, method, this.modalData.extraImgsId);
        }
      }
      this.$store.commit("marketPlace/setExtraImgs", this.modalData.extraImgs);
    },
    setHoursQuantity(value) {
      this.hoursQuantityInput = value;
    }
  },
  components: {
    MPActionBtn,
    MPInput,
    InfoBlock
  },
  computed: {
    getModalInfo() {
      return [[{
        title: "Назва оголошення",
        value: this.modalData?.name || ""
      }], [{
        title: "Ціна вашого оголошення",
        value: `${formatNumber(this.modalData?.price, " ")} $` || ""
      }], [{
        title: "Мінімальна кількість годин для розміщення",
        value: `${this.getListingMinPostingHours} ${this.$t("г.")}`
      }], [{
        title: "Підсумкова вартість",
        value: `${this.getListingUploadPrice} $`
      }]];
    },
    getListingMinPostingHours() {
      return this.$store.getters["marketPlace/getListingMinPostingHours"];
    },
    getListingMaxPostingHours() {
      return this.$store.getters["marketPlace/getListingMaxPostingHours"];
    },
    getListingPricePerHour() {
      return this.$store.getters["marketPlace/getListingPricePerHour"];
    },
    getListingUploadPrice() {
      return formatNumber(this.hoursQuantityInput * this.getListingPricePerHour, " ") || 0;
    }
  }
};
